require('workflows.php');
require('search.php');

//number of streams sorted by number of viewers descending (max 100)
$limit = 50; 

$w = new Workflows();
$s = getTwitchStreams( $limit );

if($s != null) {
	foreach ($s as $key => $sele) {
    		$w->result(	$key, 
					$sele['url'], 
					$sele['streamer'] . '   -   ' . $sele['title'], 
					$sele['viewers'] . ' Viewers - ' . $sele['game'], 
					'icon.png',
					'yes' 
				  );
	}
}
else {
	 $w->result( '', '', 'No streams online? This is impossible!', 
                 'Either you or twitch.tv is offline! Check your ' .
                 'internet connection first.', 'icon.png', 'no' );
}

echo $w->toxml();

require('workflows.php');
require('search.php');

//number of games sorted by popularity (max 100)
$limit = 50; 
$q = urlencode( "{query}" );

$w = new Workflows();
$s = searchGame( $q, $limit );

if($s != null) {
	foreach ($s as $key => $sele) {
    		$w->result(	$key, 
					'', 
					$sele['name'], 
					'Popularity: ' . $sele['popularity'], 
					'icon.png',
					'yes',
					$sele['name']
				  );
	}
}
else {
	 $w->result( '', '', 'No stream or game found!', 
                 'Try again!', 'icon.png', 'no' );
}
echo $w->toxml();

require('workflows.php');
require('search.php');

//number of streams sorted by number of viewers descending (max 100)
$limit = 25; 
$q = urlencode( "{query}" );

$w = new Workflows();
$s = searchStream( $q, $limit );

if($s != null) {
	foreach ($s as $key => $sele) {
    		$w->result(	$key, 
					$sele['url'], 
					$sele['streamer'] . '   -   ' . $sele['title'], 
					$sele['viewers'] . ' Viewers - ' . $sele['game'], 
					'icon.png',
					'yes' 
				  );
	}
}
elseif( strlen( $q ) < 3 ) {
	 $w->result( '', '', 'Please wait for result or make sure you have ' . 
              'typed in at least 3 letters.', 
              'Searching for stream(s) on twitch.tv ...','icon.png','no' );
}
else {
	 $w->result( '', '', 'No stream or game found! Try another!', 
     'Stream is probably offline or you made a typo.', 'icon.png', 'no' );
}
echo $w->toxml();
