dict.string:
require('workflows.php');
require('simple_html_dom.php');
$w = new Workflows();

$in  = urlencode("{query}");
$path = "http://MY_WEB_SERVER/twitch/";
$url = $path . "ALL.php?game=".$in;

$html = file_get_html( $url );
$first = true; //only for this version

foreach($html->find( 'tr' ) as $row) {
	if ( $first ):
		$first = false;
		continue;
	endif;

	$game = $row->find( 'td.game',0 )->plaintext;

	//$avatar = $row->find( 'img.avatarimg',0 );
	//$avatar2 = $avatar->src;
	//$gameicon = $path.''.$game2;

	$viewers = $row->find( 'td.viewers',0 );
	$title = $row->find( 'td.title',0 );

	$streamerUrl = $row->find( 'td.url',0 );	
	$streamUrl = $row->find( 'a',0 )->href;	

	$w->result( 'Searching TwitchStreamer', 
				$streamUrl, 
				$title->plaintext, 
				$viewers->plaintext . ' Viewers - ' . $game, 
				'icon.png',
				'yes' 
			);
}

if ( count( $w->results() ) == 0 ) {
	$w->result( '', '', 'Game not found or no one is playing it. Try another!', ':-(', 'icon.png', 'no' );
}

echo $w->toxml();
